import React, { Component } from 'react'


export default class Hangghe extends Component {

    renderGhe = () => {
        return (
            // console.log(this.props.hangGhe.danhSachGhe)
            this.props.hangGhe.danhSachGhe.map((ghe,index) => {
            // if (index ===0) {
            //     return <span className='rowNumber'>{ghe.soGhe}</span>
            // }
                let cssgheDaDat = ''
                let disabled = false;
                if (ghe.daDat) {
                    cssgheDaDat = 'gheDuocChon'
                    disabled = true
                }
                return <button disabled={disabled} onClick={()=>{
                    alert('fasdjf')
                }} className={`ghe ${cssgheDaDat}`} key={index}>
                    {ghe.soGhe}
                </button>

            } ) 
        )
    }
    //renderfull hàng ghế 
    renderHangGhe = ()=> {
        if (this.props.soHangGhe === 0){
            return ''
        }

        else {
            return (
                this.props.hangGhe.hang,
                this.renderGhe()
            )
        }

    }
  render() {

    return (

      <div className='ml-4 text-light font-weight-bold text-left' style={{fontSize: '25px'}}>
         {this.renderHangGhe()}

          </div>
    )
  }
}
