import React, { Component } from 'react'


export default class Hangghe extends Component {

    renderGhe = () => {
        return (
            // console.log(this.props.hangGhe.danhSachGhe)
            this.props.hangGhe.danhSachGhe.map((ghe,index) => {
           //trạng thái ghế bị người khác đặt rồi 
                let cssgheDaDat = ''
                let disabled = false;
                if (ghe.daDat) {
                    cssgheDaDat = 'gheDuocChon'
                    disabled = true
                }
                //xét trạng thái ghế đang đặt => thêm css khi click vào nó hiện mào xanh
                return <button  disabled={disabled} onClick={()=>{
                    alert('fasdjf')
                }} className={`ghe ${cssgheDaDat} `} key={index}>
                    {ghe.soGhe}
                </button>

            } ) 
        )
    }
    renderSoHang = () => {
        return this.props.hangGhe.danhSachGhe.map((hang,index) => {
            return (
                <button className='rowNumber' >{hang.soGhe}</button>
            )
        })
    }
    //renderfull hàng ghế 
    renderHangGhe = ()=> {
        if (this.props.soHangGhe === 0){
            return (
                <div>
                    {this.props.hangGhe.hang}
                {this.renderSoHang()}
                </div>
            )

        }

        else {
            return (
                <div>
                    {this.props.hangGhe.hang}
                {this.renderGhe()}
                </div>
            )

        }

    }
  render() {

    return (

      <div className='ml-4 text-light font-weight-bold text-center' style={{fontSize: '25px'}}>
         {this.renderHangGhe()}
          </div>
    )
  }
}
