import React, { Component } from 'react'


export default class Hangghe extends Component {

    renderGhe = () => {
        return (
            // console.log(this.props.hangGhe.danhSachGhe)
            this.props.hangGhe.danhSachGhe.map((ghe,index) => {
                let cssgheDaDat = ''
                let disabled = false;
                if (ghe.daDat) {
                    cssgheDaDat = 'gheDuocChon'
                    disabled = true
                }
                return <button className={`ghe ${cssgheDaDat} ${disabled}`} key={index}>
                    {ghe.soGhe}
                </button>

            } ) 
        )
    }
  render() {
      const {hangGhe} =this.props

    return (

      <div className='ml-4 text-light font-weight-bold text-left' style={{fontSize: '25px'}}>
          {hangGhe.hang}
          {this.renderGhe()}
          </div>
    )
  }
}
